report.sql:


SELECT 

  sum(COALESCE(openedcount, 0)) as openedcount, 
  sum(COALESCE(closedcount, 0)) as closedcount, 
  sum(COALESCE(handlercount, 0)) as handlercount, 
  sum(COALESCE(usercount, 0)) as usercount, 
  sum(COALESCE(totalcount, 0)) as totalcount, 
  {dategroup_disp} as display, 
  date

FROM #__fss_date_day as baseday

LEFT JOIN 
  (
    SELECT count(*) as openedcount, DATE(opened) as opened
    FROM #__fss_ticket_ticket as t
    WHERE 
        opened BETWEEN '{opened_from} 00:00:00' AND '{opened_to} 23:59:59'
    {if,product}
        AND
            {product}
    {endif}

    {if,department}
        AND
            {department}
    {endif}

    {if,user,'unreg'}
      AND 
          t.user_id = '0'
    {endif}
    {if,user,'unreg',not}
      {if,user,'all',not}
        {if,user}
        AND 
            t.user_id = '{user}'
        {endif}
      {endif}
    {endif}


    {if,handler,'unassigned'}
      AND
          t.admin_id = '0'
    {endif}
    {if,handler,'unassigned',not}
      {if,handler,'all',not}
      AND 
          t.admin_id = '{handler}'
      {endif}
    {endif}

    AND t.source != 'email' AND t.source != 'email_declined'

    GROUP BY DATE(opened)
  ) AS opened
  ON baseday.date = opened.opened

LEFT JOIN 
  (
    SELECT count(*) as closedcount, DATE(closed) as closed
    FROM #__fss_ticket_ticket as t
    WHERE 
        opened BETWEEN '{opened_from} 00:00:00' AND '{opened_to} 23:59:59'

    {if,product}
        AND
            {product}
    {endif}

    {if,department}
        AND
            {department}
    {endif}

    {if,user,'unreg'}
      AND 
          t.user_id = '0'
    {endif}
    {if,user,'unreg',not}
      {if,user,'all',not}
        {if,user}
        AND 
            t.user_id = '{user}'
        {endif}
      {endif}
    {endif}


    {if,handler,'unassigned'}
      AND
          t.admin_id = '0'
    {endif}
    {if,handler,'unassigned',not}
      {if,handler,'all',not}
      AND 
          t.admin_id = '{handler}'
      {endif}
    {endif}

    AND t.source != 'email' AND t.source != 'email_declined' 

    GROUP BY DATE(closed)
  ) AS closed
  ON baseday.date = closed.closed

LEFT JOIN 
  (
    SELECT count(*) as handlercount, DATE(posted) as posted
    FROM #__fss_ticket_messages as m
    WHERE 
        posted BETWEEN '{opened_from} 00:00:00' AND '{opened_to} 23:59:59'
    AND
        m.admin IN (1, 2)

    {if,handler,'unassigned',not}
      {if,handler,'all',not}
      AND 
          m.user_id = '{handler}'
      {endif}
    {endif}

    {if,department}
        AND
            m.ticket_ticket_id IN (SELECT id FROM #__fss_ticket_ticket WHERE {department} AND source != 'email' AND source != 'email_declined')
    {endif}

    {if,product}
        AND
            m.ticket_ticket_id IN (SELECT id FROM #__fss_ticket_ticket WHERE {product} AND source != 'email' AND source != 'email_declined')
    {endif}

    GROUP BY DATE(posted)
  ) AS handlermc
  ON baseday.date = handlermc.posted

LEFT JOIN 
  (
    SELECT count(*) as usercount, DATE(posted) as posted
    FROM #__fss_ticket_messages as m
    WHERE 
        posted BETWEEN '{opened_from} 00:00:00' AND '{opened_to} 23:59:59'
    AND
        m.admin = 0

    {if,user,'unreg'}
      AND 
          m.user_id = '0'
    {endif}
    {if,user,'unreg',not}
      {if,user,'all',not}
        {if,user}
        AND 
            m.user_id = '{user}'
        {endif}
      {endif}
    {endif}

    {if,department}
        AND
            m.ticket_ticket_id IN (SELECT id FROM #__fss_ticket_ticket WHERE {department} AND source != 'email' AND source != 'email_declined')
    {endif}

    {if,product}
        AND
            m.ticket_ticket_id IN (SELECT id FROM #__fss_ticket_ticket WHERE {product} AND source != 'email' AND source != 'email_declined')
    {endif}

    GROUP BY DATE(posted)
  ) AS usermc
  ON baseday.date = usermc.posted

LEFT JOIN 
  (
    SELECT count(*) as totalcount, DATE(posted) as posted
    FROM #__fss_ticket_messages as m
    WHERE 
        posted BETWEEN '{opened_from} 00:00:00' AND '{opened_to} 23:59:59'
    AND
        m.admin IN (0, 1, 2)

    {if,department}
        AND
            m.ticket_ticket_id IN (SELECT id FROM #__fss_ticket_ticket WHERE {department} AND source != 'email' AND source != 'email_declined')
    {endif}

    {if,product}
        AND
            m.ticket_ticket_id IN (SELECT id FROM #__fss_ticket_ticket WHERE {product} AND source != 'email' AND source != 'email_declined')
    {endif}


    GROUP BY DATE(posted)
  ) AS totalmc
  ON baseday.date = totalmc.posted

WHERE 

baseday.date BETWEEN '{opened_from} 00:00:00' AND '{opened_to} 23:59:59'

AND 

baseday.date >= (SELECT MIN(opened) as mindate FROM #__fss_ticket_ticket)

GROUP BY {dategroup}

ORDER BY date DESC
