                EasySocial.ajax('apps/event/tasks/controllers/milestone/resolve', {
                    id: self.options.milestoneId,
                    eventId: self.options.eventId
                })
                .done(function() {
                    self.wrapper().removeClass('is-due').addClass('is-completed');
                });
